mapper.select:
select count(aptCode) 
from houseinfo 
where 1 = 1 

AND aptName LIKE CONCAT('%', #{aptKeyword}, '%')  


AND left(dongCode,2) = #{sidoCode} 


AND left(dongCode, 5) = #{gugunCode} 


AND dongCode = #{dongCode}

select h.aptCode, h.aptName, h.buildyear, h.dongName, h.jibun, si.sidoname, gu.gugunname, h.lat, h.lng, (select dealAmount from housedeal where aptCode = h.aptCode and no = (select max(no) from housedeal where aptCode = h.aptCode)) recentPrice , (select max(dealAmount) from housedeal where aptCode = h.aptCode) as maxPrice, (select count(dealAmount) from housedeal where aptCode = h.aptCode) as dealCount, 1 as isUserInterest from houseinfo h left join interest_apart i on h.aptCode = i.aptCode left join sidocode si on left(h.dongcode,2) = left(si.sidocode,2) left join guguncode gu on left(h.dongcode,5) = left(gu.guguncode,5) where i.user_id = #{userId}
AND aptName LIKE CONCAT('%', #{aptKeyword}, '%')
AND left(dongCode,2) = #{sidoCode}
AND left(dongCode, 5) = #{gugunCode}
AND dongCode = #{dongCode}
ORDER BY
dealCount desc
h.buildYear desc
maxPrice desc
recentPrice desc
h.aptName
h.aptCode
LIMIT #{limit} OFFSET #{offset}

select count(h.aptCode) 
from houseinfo  h
left join interest_apart i
on h.aptCode = i.aptCode
where i.user_id = #{userId} 

AND aptName LIKE CONCAT('%', #{aptKeyword}, '%')  


AND left(dongCode,2) = #{sidoCode} 


AND left(dongCode, 5) = #{gugunCode} 


AND dongCode = #{dongCode}

select no, aptCode, dealAmount, dealYear, dealMonth, dealDay, area, floor, type, rentMoney
from housedeal
where aptCode = #{aptCode}
